import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', { email, password });
      if (response.data.success) {
        localStorage.setItem('userId', response.data.user_id);
        localStorage.setItem('username', response.data.username);
        navigate(response.data.role === 'admin' ? '/admin' : '/booking');
      } else {
        setError(response.data.message || 'Login failed');
      }
    } catch (err) {
      setError('Network error or server not reachable');
    }
  };

  return (
    <div style={styles.pageWrapper}>
      <div style={styles.card}>
        <h1 style={styles.mainTitle}>Smart Train Ticket Reservation</h1>
        <p style={styles.subTitle}>Please login to continue</p>

        <form onSubmit={handleLogin} style={styles.form}>
          <div style={styles.inputGroup}>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
              style={styles.input}
            />
          </div>

          <div style={styles.inputGroup}>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
              style={styles.input}
            />
          </div>

          <button type="submit" style={styles.loginButton}>Login</button>

          {error && <p style={styles.errorText}>{error}</p>}
        </form>

        <p style={styles.registerText}>
          Don't have an account?{' '}
          <Link to="/register" style={styles.registerLink}>Register here</Link>
        </p>
      </div>
    </div>
  );
};

// Updated Styles
const styles = {
  pageWrapper: {
    minHeight: '100vh',
    background: 'linear-gradient(to right, #74ebd5, #ACB6E5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '20px',
  },
  card: {
    width: '100%',
    maxWidth: '400px',
    backgroundColor: '#fff',
    borderRadius: '12px',
    padding: '40px 30px',
    boxShadow: '0px 10px 25px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
  },
  mainTitle: {
    fontSize: '24px',
    fontWeight: '700',
    color: '#333',
    marginBottom: '10px',
    fontFamily: 'Poppins, sans-serif',
  },
  subTitle: {
    fontSize: '16px',
    color: '#666',
    marginBottom: '30px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
  },
  inputGroup: {
    width: '100%',
  },
  input: {
    width: '100%',
    padding: '12px 15px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '14px',
    outline: 'none',
    transition: 'border-color 0.3s',
  },
  loginButton: {
    padding: '12px 0',
    backgroundColor: '#4CAF50',
    color: '#fff',
    fontSize: '16px',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  errorText: {
    marginTop: '10px',
    color: 'red',
    fontSize: '14px',
  },
  registerText: {
    marginTop: '20px',
    fontSize: '14px',
    color: '#555',
  },
  registerLink: {
    color: '#4CAF50',
    fontWeight: 'bold',
    textDecoration: 'none',
  },
};

export default Login;
