import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './Login.css'; // <-- We move styles to a separate CSS file

const profiles = [
  { src: 'https://cdn-icons-png.freepik.com/256/3135/3135823.png?semt=ais_hybrid', name: 'Aarthi' },
  { src: 'https://cdn-icons-png.freepik.com/256/3135/3135823.png?semt=ais_hybrid', name: 'Divya' },
  { src: 'https://cdn-icons-png.freepik.com/256/3135/3135823.png?semt=ais_hybrid', name: 'Ramya' },
  { src: 'https://cdn-icons-png.freepik.com/256/3135/3135823.png?semt=ais_hybrid', name: 'Renuga' },
];

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', { email, password });
      if (response.data.success) {
        localStorage.setItem('userId', response.data.user_id);
        localStorage.setItem('username', response.data.username);
        if (response.data.role === 'admin') {
          navigate('/admin');
        } else {
          navigate('/booking');
        }
      } else {
        setError('Entered username and password is wrong');
      }
    } catch (err) {
      setError('Entered username and password is wrong');
    }
  };

  return (
    <div className="pageContainer">
      <div className="trainAnimation"></div> {/* ðŸ‘ˆ Train animation background */}
      <div className="loginContainer">
        <h1 className="heading">Smart Train Ticket Reservation System</h1>
        <h2 className="subHeading">Login</h2>
        <form onSubmit={handleLogin} className="form">
          <input
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            placeholder="Email"
            className="input"
          />
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            placeholder="Password"
            className="input"
          />
          <button type="submit" className="button">Login</button>
          {error && <p className="errorText">{error}</p>}
        </form>
        <p className="footerText">
          Don't have an account? <Link to="/register" className="link">Register here</Link>
        </p>
        <p className="footerText">
          <b>Team Members</b>
        </p>
        <div className="profileIconsContainer">
          {profiles.map((profile, index) => (
            <div key={index} className="profileItem">
              <img src={profile.src} alt={`Profile ${index + 1}`} className="profileIcon" />
              <p className="profileName">{profile.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Login;
