import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Booking = () => {
  const [cities, setCities] = useState([]);
  const [trains, setTrains] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);
  const [seats, setSeats] = useState([]);
  const [selectedTrain, setSelectedTrain] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [sourceCity, setSourceCity] = useState('');
  const [destinationCity, setDestinationCity] = useState('');
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    axios.get('/api/cities').then(res => {
      if (res.data.success) {
        setCities(res.data.cities);
      }
    });
  }, []);

  useEffect(() => {
    if (sourceCity) {
      axios.get(`/api/trains/${sourceCity}`).then(res => {
        if (res.data.success) {
          setTrains(res.data.trains);
        }
      });
    } else {
      setTrains([]);
    }
    setSelectedTrain('');
    setTimeSlots([]);
    setSelectedTimeSlot('');
    setSeats([]);
    setSelectedSeats([]);
  }, [sourceCity]);

  useEffect(() => {
    if (selectedTrain) {
      axios.get(`/api/time_slots/${selectedTrain}`).then(res => {
        if (res.data.success) {
          setTimeSlots(res.data.time_slots);
        }
      });
    } else {
      setTimeSlots([]);
    }
    setSelectedTimeSlot('');
    setSeats([]);
    setSelectedSeats([]);
  }, [selectedTrain]);

  useEffect(() => {
    if (selectedTrain && selectedTimeSlot) {
      axios.get(`/api/seats/${selectedTrain}/${selectedTimeSlot}`).then(res => {
        if (res.data.success) {
          setSeats(res.data.seats);
        }
      });
    } else {
      setSeats([]);
    }
    setSelectedSeats([]);
  }, [selectedTrain, selectedTimeSlot]);

  const toggleSeatSelection = (seatId) => {
    if (selectedSeats.includes(seatId)) {
      setSelectedSeats(selectedSeats.filter(id => id !== seatId));
    } else {
      setSelectedSeats([...selectedSeats, seatId]);
    }
  };

  const handleBooking = () => {
    if (!sourceCity || !destinationCity) {
      alert('Please select source and destination cities');
      return;
    }
    if (!selectedTrain || !selectedTimeSlot || selectedSeats.length === 0) {
      alert('Please select train, time slot, and seats');
      return;
    }
    const userId = localStorage.getItem('userId'); // Assuming userId is stored in localStorage after login
    const username = localStorage.getItem('username'); // Assuming username is stored in localStorage after login

    if (!userId || !username) {
      alert('User not logged in. Please login to book seats.');
      return;
    }

    selectedSeats.forEach(seatId => {
      axios.post('/api/book', {
        user_id: userId,
        username: username,
        train_id: selectedTrain,
        time_slot_id: selectedTimeSlot,
        seat_id: seatId,
        source_city: sourceCity,
        destination_city: destinationCity
      }).then(res => {
        console.log('Booking response:', res);
        if (res.data.success) {
          alert('Booking successful');
          // Refresh bookings or seats
          axios.get(`/api/seats/${selectedTrain}/${selectedTimeSlot}`).then(res => {
            if (res.data.success) {
              setSeats(res.data.seats);
            }
          });
          fetchUserBookings();
        } else {
          alert(res.data.message || 'Booking failed');
        }
      }).catch(error => {
        console.error('Booking error:', error);
        alert('Booking failed due to network error');
      });
    });
  };

  const fetchUserBookings = () => {
    const username = localStorage.getItem('username');
    if (!username) {
      setBookings([]);
      return;
    }
    axios.get('/api/bookings', { params: { username, role: 'user' } }).then(res => {
      if (res.data.success) {
        setBookings(res.data.bookings);
      }
    });
  };

  useEffect(() => {
    fetchUserBookings();
  }, []);

  return (
    <div>
      <h2>Book Your Train Ticket</h2>
      <div>
        <label>Source City:</label>
        <select value={sourceCity} onChange={e => setSourceCity(e.target.value)}>
          <option value="">Select Source City</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Destination City:</label>
        <select value={destinationCity} onChange={e => setDestinationCity(e.target.value)}>
          <option value="">Select Destination City</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Train:</label>
        <select value={selectedTrain} onChange={e => setSelectedTrain(e.target.value)}>
          <option value="">Select Train</option>
          {trains.map(train => (
            <option key={train.id} value={train.id}>{train.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Time Slot:</label>
        <select value={selectedTimeSlot} onChange={e => setSelectedTimeSlot(e.target.value)}>
          <option value="">Select Time Slot</option>
          {timeSlots.map(slot => (
            <option key={slot.id} value={slot.id}>{slot.slot_time}</option>
          ))}
        </select>
      </div>
      <div>
        <h3>Seats</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
          {seats.map(seat => (
            <div
              key={seat.id}
              onClick={() => seat.is_available && toggleSeatSelection(seat.id)}
              style={{
                margin: '5px',
                padding: '10px',
                backgroundColor: selectedSeats.includes(seat.id) ? 'green' : seat.is_available ? 'lightgray' : 'red',
                color: seat.is_available ? 'black' : 'white',
                cursor: seat.is_available ? 'pointer' : 'not-allowed',
                opacity: seat.is_available ? 1 : 0.5,
                borderRadius: '4px',
                userSelect: 'none'
              }}
            >
              {seat.seat_number}
            </div>
          ))}
        </div>
      </div>
      <button onClick={handleBooking}>Book Selected Seats</button>

      <h3>Your Bookings</h3>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>Booking ID</th>
            <th>Train</th>
            <th>Time Slot</th>
            <th>Seat</th>
            <th>Source City</th>
            <th>Destination City</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map(booking => (
            <tr key={booking.id}>
              <td>{booking.id}</td>
              <td>{booking.train_name}</td>
              <td>{booking.time_slot}</td>
              <td>{booking.seat_name}</td>
              <td>{booking.source_city_name}</td>
              <td>{booking.destination_city_name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Booking;
