import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Booking = () => {
  const [cities, setCities] = useState([]);
  const [trains, setTrains] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);
  const [seats, setSeats] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedTrain, setSelectedTrain] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [selectedSeat, setSelectedSeat] = useState('');
  const [bookings, setBookings] = useState([]);
  const [username, setUsername] = useState('');

  useEffect(() => {
    axios.get('/api/cities').then(res => {
      if (res.data.success) {
        setCities(res.data.cities);
      }
    });

    const storedUsername = localStorage.getItem('username');
    if (storedUsername) {
      setUsername(storedUsername);
      fetchUserBookings(storedUsername);
    }
  }, []);

  const fetchTrains = (cityId) => {
    setSelectedTrain('');
    setSelectedTimeSlot('');
    setSelectedSeat('');
    setTrains([]);
    setTimeSlots([]);
    setSeats([]);
    axios.get(`/api/trains/${cityId}`).then(res => {
      if (res.data.success) {
        setTrains(res.data.trains);
      }
    });
  };

  const fetchTimeSlots = (trainId) => {
    setSelectedTimeSlot('');
    setSelectedSeat('');
    setTimeSlots([]);
    setSeats([]);
    axios.get(`/api/time_slots/${trainId}`).then(res => {
      if (res.data.success) {
        setTimeSlots(res.data.time_slots);
      }
    });
  };

  const fetchSeats = (trainId, timeSlotId) => {
    setSelectedSeat('');
    setSeats([]);
    axios.get(`/api/seats/${trainId}/${timeSlotId}`).then(res => {
      if (res.data.success) {
        setSeats(res.data.seats);
      }
    });
  };

  const fetchUserBookings = (username) => {
    axios.get('/api/bookings', { params: { username } }).then(res => {
      if (res.data.success) {
        setBookings(res.data.bookings);
      }
    });
  };

  const handleCityChange = (e) => {
    const cityId = e.target.value;
    setSelectedCity(cityId);
    fetchTrains(cityId);
  };

  const handleTrainChange = (e) => {
    const trainId = e.target.value;
    setSelectedTrain(trainId);
    fetchTimeSlots(trainId);
  };

  const handleTimeSlotChange = (e) => {
    const timeSlotId = e.target.value;
    setSelectedTimeSlot(timeSlotId);
    fetchSeats(selectedTrain, timeSlotId);
  };

  const handleSeatSelect = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleBooking = () => {
    if (!selectedCity || !selectedTrain || !selectedTimeSlot || !selectedSeat) {
      alert('Please select city, train, time slot, and seat.');
      return;
    }
    const userId = localStorage.getItem('user_id');
    axios.post('/api/book', {
      user_id: userId,
      username,
      train_id: selectedTrain,
      time_slot_id: selectedTimeSlot,
      seat_id: selectedSeat,
      source_city: selectedCity,
      destination_city: selectedCity
    }).then(res => {
      if (res.data.success) {
        alert('Booking successful!');
        fetchSeats(selectedTrain, selectedTimeSlot);
        fetchUserBookings(username);
      } else {
        alert(res.data.message || 'Booking failed');
      }
    });
  };

  return (
    <div>
      <h2>Book Your Train Ticket</h2>
      <div>
        <label>City:</label>
        <select value={selectedCity} onChange={handleCityChange}>
          <option value="">Select City</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Train:</label>
        <select value={selectedTrain} onChange={handleTrainChange} disabled={!selectedCity}>
          <option value="">Select Train</option>
          {trains.map(train => (
            <option key={train.id} value={train.id}>{train.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Time Slot:</label>
        <select value={selectedTimeSlot} onChange={handleTimeSlotChange} disabled={!selectedTrain}>
          <option value="">Select Time Slot</option>
          {timeSlots.map(slot => (
            <option key={slot.id} value={slot.id}>{slot.slot_time}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Seats:</label>
        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
          {seats.map(seat => (
            <button
              key={seat.id}
              disabled={!seat.is_available}
              style={{
                margin: '5px',
                backgroundColor: seat.id === selectedSeat ? 'green' : seat.is_available ? 'lightgray' : 'red',
                color: seat.is_available ? 'black' : 'white',
                opacity: seat.is_available ? 1 : 0.5,
                cursor: seat.is_available ? 'pointer' : 'not-allowed'
              }}
              onClick={() => handleSeatSelect(seat.id)}
            >
              {seat.seat_number}
            </button>
          ))}
        </div>
      </div>
      <button onClick={handleBooking}>Book Seat</button>

      <h3>Your Bookings</h3>
      <ul>
        {bookings.map(booking => (
          <li key={booking.id}>
            Seat {booking.seat_id} on Train {booking.train_id} from {booking.source_city_name} to {booking.destination_city_name} at {booking.created_at}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Booking;
