// Internal CSS styles
const styles = {
  pageContainer: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    padding: '20px',
  },
  registerContainer: {
    backgroundColor: '#ffffff',
    padding: '40px 30px',
    borderRadius: '15px',
    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.15)',
    width: '100%',
    maxWidth: '400px',
    animation: 'fadeIn 0.8s ease-in-out',
  },
  heading: {
    textAlign: 'center',
    marginBottom: '25px',
    fontSize: '28px',
    color: '#333333',
    fontWeight: '600',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  label: {
    marginBottom: '5px',
    fontSize: '14px',
    color: '#555555',
    fontWeight: '500',
  },
  input: {
    padding: '12px 15px',
    borderRadius: '8px',
    border: '1px solid #ddd',
    fontSize: '14px',
    outline: 'none',
    transition: 'all 0.3s ease',
  },
  button: {
    marginTop: '10px',
    padding: '12px',
    backgroundColor: '#5A67D8',
    color: '#ffffff',
    fontSize: '16px',
    fontWeight: '600',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  footerText: {
    marginTop: '20px',
    fontSize: '14px',
    textAlign: 'center',
    color: '#777777',
  },
  link: {
    color: '#5A67D8',
    textDecoration: 'none',
    fontWeight: '600',
  },
};

// Adding focus/hover styles through a small CSS-in-JS trick
styles.input[':focus'] = {
  borderColor: '#5A67D8',
  boxShadow: '0 0 5px rgba(90, 103, 216, 0.5)',
};

styles.button[':hover'] = {
  backgroundColor: '#434190',
};
