import React, { useState, useEffect } from 'react';
import axios from 'axios';

axios.defaults.baseURL = '/api';

function Booking() {
  const [cities, setCities] = useState([]);
  const [selectedSourceCity, setSelectedSourceCity] = useState('');
  const [selectedDestinationCity, setSelectedDestinationCity] = useState('');
  const [trains, setTrains] = useState([]);
  const [selectedTrain, setSelectedTrain] = useState(null);
  const [timeSlots, setTimeSlots] = useState([]);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [userId, setUserId] = useState(null); // This should be set after login, here for demo
  const [username, setUsername] = useState(''); // This should be set after login, here for demo

  useEffect(() => {
    // Fetch cities on mount
    axios.get('/cities')
      .then(res => {
        if (res.data.success) {
          setCities(res.data.cities);
        }
      });
    // For demo, set userId and username to dummy values
    setUserId(1);
    setUsername('ram');
  }, []);

  useEffect(() => {
    if (selectedSourceCity) {
      axios.get(`/trains/${selectedSourceCity}`)
        .then(res => {
          if (res.data.success) {
            setTrains(res.data.trains);
            setSelectedTrain(null);
            setTimeSlots([]);
            setSelectedTimeSlot(null);
            setSeats([]);
            setSelectedSeat(null);
          }
        });
    }
  }, [selectedSourceCity]);

  useEffect(() => {
    if (selectedTrain) {
      axios.get(`/time_slots/${selectedTrain}`)
        .then(res => {
          if (res.data.success) {
            setTimeSlots(res.data.time_slots);
            setSelectedTimeSlot(null);
            setSeats([]);
            setSelectedSeat(null);
          }
        });
    }
  }, [selectedTrain]);

  useEffect(() => {
    if (selectedTrain && selectedTimeSlot) {
      axios.get(`/seats/${selectedTrain}/${selectedTimeSlot}`)
        .then(res => {
          if (res.data.success) {
            setSeats(res.data.seats);
            setSelectedSeat(null);
          }
        });
    }
  }, [selectedTrain, selectedTimeSlot]);

  const handleSeatSelect = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleBooking = () => {
    if (!userId || !username || !selectedTrain || !selectedTimeSlot || !selectedSeat || !selectedSourceCity || !selectedDestinationCity) {
      alert('Please select source city, destination city, train, time slot, and seat.');
      return;
    }
    axios.post('/book', {
      user_id: userId,
      username: username,
      train_id: selectedTrain,
      time_slot_id: selectedTimeSlot,
      seat_id: selectedSeat,
      source_city: selectedSourceCity,
      destination_city: selectedDestinationCity
    }).then(res => {
      if (res.data.success) {
        alert('Seat booked successfully!');
        // Refresh seats
        axios.get(`/seats/${selectedTrain}/${selectedTimeSlot}`)
          .then(res => {
            if (res.data.success) {
              setSeats(res.data.seats);
              setSelectedSeat(null);
            }
          });
      } else {
        alert(res.data.message || 'Booking failed');
      }
    }).catch(() => {
      alert('Booking failed');
    });
  };

  return (
    <div className="booking-container">
      <h2>Book Your Train Ticket</h2>
      <div>
        <label>Source City:</label>
        <select onChange={e => setSelectedSourceCity(e.target.value)} value={selectedSourceCity}>
          <option value="" disabled>Select Source City</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Destination City:</label>
        <select onChange={e => setSelectedDestinationCity(e.target.value)} value={selectedDestinationCity}>
          <option value="" disabled>Select Destination City</option>
          {cities.map(city => (
            <option key={city.id} value={city.id}>{city.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Train:</label>
        <select onChange={e => setSelectedTrain(e.target.value)} value={selectedTrain || ''} disabled={!selectedSourceCity}>
          <option value="" disabled>Select Train</option>
          {trains.map(train => (
            <option key={train.id} value={train.id}>{train.name}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Time Slot:</label>
        <select onChange={e => setSelectedTimeSlot(e.target.value)} value={selectedTimeSlot || ''} disabled={!selectedTrain}>
          <option value="" disabled>Select Time Slot</option>
          {timeSlots.map(slot => (
            <option key={slot.id} value={slot.id}>{slot.slot_time}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Seats:</label>
        <div style={{ display: 'flex', flexWrap: 'wrap', maxWidth: '300px' }}>
          {seats.map(seat => (
            <div
              key={seat.id}
              onClick={() => seat.is_available && handleSeatSelect(seat.id)}
              style={{
                margin: '5px',
                padding: '10px',
                border: '1px solid #ccc',
                backgroundColor: seat.is_available ? (seat.id === selectedSeat ? 'lightgreen' : 'white') : 'lightgray',
                cursor: seat.is_available ? 'pointer' : 'not-allowed',
                opacity: seat.is_available ? 1 : 0.5,
                userSelect: 'none'
              }}
            >
              {seat.seat_number}
            </div>
          ))}
        </div>
      </div>
      <button onClick={handleBooking} disabled={!selectedSeat}>Book Seat</button>
    </div>
  );
}

export default Booking;
